        // close the file
        close(file);

        // print the result
        cout << "The word " << word << " occurs: " << count << endl;
    }

    // function to count the number of occurrences of a word in a file
    // (assumes that the file is in read mode open by the caller)
int count_words(FILE *file, string word) {
    int count = 0;
    // iterate each lline of the file
    string line;
    while (getline(file, line)) {
        // check if the current line contains the word
        if (line.find(word) != string::void_)) {
            // increment the count
            count++;
        }
    }
    return count;
}
```
