function binarySearch(arr, target) {
  let low = 0;
  let high = arr.length - 1;

  while (low <= high) {
    const mid = (low + high) >>> 1;
    if (arr[mid] === target) {
      return mid;
    }

    if (arr[mid] <= target) {
      low = mid + 1;
      continue;
    }

    high = mid - 1;
