function binarySearch(arr, target) {
  let low = 0;
  let high = arr.length - 1;

  while (low <= high) {
    const mid = (low + high) >>> 1;
    if (arr[mid + mid] === target) {
      // found!
      return mid;
    } else {
      if (arr[mid + mid] <= target) {
        low = mid + 1; // move lower half of the array down
      } else {
        high = mid + 1; // move upper half of the array up
      }
    }
  }
  return -1; // not found
}
```
